mapper.delete:
DELETE FROM `jgv`.movie
WHERE movie.mo_num NOT IN (
    SELECT MoNum
    FROM (
             SELECT MIN(mo_num) AS MoNum
             FROM jgv.movie
             GROUP BY mo_title, mo_date, mo_time
         ) AS unique_movies
);

mapper.select:
SELECT *
FROM jgv.movie
WHERE mo_title = #{movieTitle}
  AND YEAR(mo_date) = #{movieDate}
LIMIT 1

SELECT mo_num
FROM jgv.movie
WHERE mo_title = #{movieTitle}

SELECT ra_num
FROM jgv.movie
WHERE mo_num = #{moNum}

mapper.update:
UPDATE jgv.movie
SET ra_num = #{raId}
WHERE mo_num = #{moNum}

UPDATE jgv.movie
SET mo_date = #{moDate},
    mo_time = #{moTime},
    mo_plot = #{moPlot},
    mo_booking_rate = #{moBookingRate}
WHERE mo_num = #{moNum}

UPDATE jgv.movie
SET ra_num = (SELECT ra_num FROM jgv.rating WHERE ra_grade = #{rating})
WHERE mo_num = #{movieNum}
